const fs = require(''fs'');
const content = fs.readFileSync(''src/data/worldsConfig.js'', ''utf8'');

// Pattern to find themeTag lines without levels following
const pattern = /(themeTag: ""[^""]+"",.*\n)(        (?!levels:))/g;
const replacement = ''$1        levels: [""Beginner"", ""Intermediate"", ""Advanced""],\n$2'';

const newContent = content.replace(pattern, replacement);

fs.writeFileSync(''src/data/worldsConfig.js'', newContent, ''utf8'');
console.log(''Added levels to all adventures'');
